import React, { Component, Fragment } from 'react'

import './signIn.css'

class SignInPage extends Component {
  state = {
    username:'',
    password:''
  }
  handleChange = (event) => {
    if (event.target === null) {
      console.log('ton pere')
      return
    }

    console.log(event)
    this.setState({[event.target.name]: event.target.value})
    console.log(this.state)
  }

  handleSubmit = () => {
    const jsonBody = {
      'username': this.state.name,
      'password': this.state.password
    }
console.log(jsonBody)
    fetch('http://localhost:8080/users/authenticate', {
      method: 'POST',
      headers: {
        'Content-type': 'application/json'
      },
      mode: 'cors',
      body: JSON.stringify(jsonBody)
    })
    .then(res => {
      //const propsCopy = this.props
      console.log('we are here')
      res.json()
      .then(jsonParsed => {
        console.log(jsonParsed)
      })
    })
    .then(error => {
      console.error(error)
    })
  }

  goToSignUp = () => {
    this.props.history.push('/signup')
  }
  render() {
    return (
      <Fragment>
      <div className="body"></div>
      <div className="grad"></div>
      <div className="header">
        <div>My<span>TodoList</span></div>
      </div>
      <br/>
      <div className="login">
  				<input type="text" placeholder="username" onChange={this.handleChange} name="name"/><br/>
  				<input type="password" placeholder="password" onChange={this.handleChange} name="password"/><br/>
          <input type="button" onClick={this.handleSubmit} value="Login"/>
          <input type="button" onClick={this.goToSignUp} value="Don't have an account ?"/>
  		</div>
      </Fragment>

  )}
}

export default SignInPage
