const initialState = {
  user: {
    username: '',
    password: '',
    emailAddress: '',
    firstName: '',
    lastName: '',
    authToken: ''
  }
}

const reducer = (state = initialState, action) => {
  const newState = {...state}
  if (action.type === 'STORE_USER') {
    newState.authToken = this.state.user
  }

  return newState
}

export default reducer
