import React, { Component } from 'react'

import './user.css'

class SignIn extends Component {
  state = {
    name: "",
    password: ""
  }

  handleChange = (event) => {
    this.setState({[event.target.name]: event.target.value})
    console.log(this.state.name)
  }

  handleSubmit = () => {
    console.log(this.state)
    const jsonBody = {
      'username': this.state.name,
      'password': this.state.password
    }
    fetch('http://localhost:8080/users/authenticate', {
      method: 'POST',
      headers: {
        'Content-type': 'application/json'
      },
      mode: 'cors',
      body: JSON.stringify(jsonBody)
    })
    .then(res => {
      //const propsCopy = this.props
      res.json()
      .then(jsonParsed => {
        console.log(jsonParsed)
      })
    })
    .then(error => {
      console.error(error)
    })
  }

  render() {
    return (
      <div className="login-wrap">
        <div className="login-html">
          <input id="tab-1" type="radio" readOnly name="tab" className="sign-in" checked/><label htmlFor="tab-1" className="tab">Sign In</label>
          <input id="tab-2" type="radio" name="tab" className="sign-up"/><label htmlFor="tab-2" className="tab">Sign Up</label>
        <div className="login-form">
          <div className="sign-in-htm">
            <div className="group">
              <label htmlFor="user" className="label">Username</label>
              <input id="user" type="text" onChange={this.handleChange} name="name" className="input"/>
            </div>
            <div className="group">
              <label htmlFor="pass" className="label">Password</label>
              <input id="pass" type="password" onChange={this.handleChange} name="password" className="input" data-type="password"/>
            </div>
            {
            //<div className="group">
            //  <input id="check" type="checkbox" className="check" checked/>
            //  <label htmlFor="check"><span className="icon"></span> Keep me Signed in</label>
            //</div>
            }
            <div className="group">
              <input type="submit" onClick={this.handleSubmit} className="button" value="Sign In"/>
            </div>
            <div className="hr">
            </div>
            <div className="foot-lnk">
              <a href="#forgot">Forgot Password?</a>
            </div>
          </div>
          <div className="sign-up-htm">
            <div className="group">
              <label htmlFor="user" className="label">Username</label>
              <input id="user" type="text" className="input"/>
            </div>
            <div className="group">
              <label htmlFor="pass" className="label">Password</label>
              <input id="pass" type="password" className="input" data-type="password"/>
            </div>
            <div className="group">
              <label htmlFor="pass" className="label">Repeat Password</label>
              <input id="pass" type="password" className="input" data-type="password"/>
            </div>
            <div className="group">
              <label htmlFor="pass" className="label">Email Address</label>
              <input id="pass" type="text" className="input"/>
            </div>
            <div className="group">
              <input type="submit" className="button" value="Sign Up"/>
            </div>
            <div className="hr">
            </div>
            <div className="foot-lnk">
              <label htmlFor="tab-1">Already Member?</label>
            </div>
          </div>
        </div>
      </div>
      </div>
    )
  }
}

export default SignIn
